mapper.select:
select p.personid, p.personpw, p.personname, p.personemail, p.loginflag
from resumemain r
LEFT OUTER JOIN person p on r.personid = p.personid
LEFT OUTER JOIN career c on r.resumeseq = c.resumeseq
LEFT OUTER JOIN education e on r.resumeseq = e.resumeseq
LEFT OUTER JOIN languagescore l on r.resumeseq = l.resumeseq
LEFT OUTER JOIN certi ct on r.resumeseq = ct.resumeseq
where c.careercategory = #{category}
and e.educationflag = #{education}
and c.careerposition = #{level}
and c.careertask = #{sector}
and l.languagetitle = #{language}
and ct.certititle = #{license}
ORDER BY r.personid asc

mapper.insert:
INSERT INTO OPTIONS(

	OPTIONSEQ
	, REQUESTSEQ
	, COMOPTION
	, RANKING

)VALUES(

	OPTIONSEQ.NEXTVAL
	,#{requestSeq}
	,#{comOption}
	,#{ranking}

)



		SELECT REQUESTSEQ.NEXTVAL FROM DUAL




	INSERT INTO REQUEST(

    	REQUESTSEQ
    	,HHID
