)

mapper.update:
UPDATE PERSONLIST
	SET
STATUS=#{status}
	WHERE
PERSONID=#{personId}		
	AND
HHID=#{hhId}

mapper.delete:
DELETE 
	FROM PERSONLIST
WHERE
	hhId=#{hhId}
AND
	LISTSEQ=#{listSeq}
