mapper.insert:


	SELECT RESUMESEQ.NEXTVAL FROM DUAL



INSERT INTO BASICINFO(

BASICSEQ
,RESUMESEQ
,PERSONID
,PERSONNAME
,BIRTHDATE
,GENDER
,PERSONEMAIL
,PERSONPHONE
,PERSONADDR

)VALUES(

BASICSEQ.NEXTVAL
,#{resumeSeq}
,#{personId}
,#{personName}
,#{birthDate}
,#{gender}
,#{personEmail}
,#{personPhone}
,#{personAddr}

)


INSERT INTO CERTI(

	CERTISEQ 
    , RESUMESEQ 
    , CERTITITLE
    , CERTIORG 
    , CERTIDATE

)VALUES(

	CERTISEQ.NEXTVAL
	,#{resumeSeq}
	,#{certiTitle}
	,#{certiOrg}
	,#{certiDate}	

)


INSERT INTO LANGUAGESCORE(

	LANGUAGESEQ 
    , RESUMESEQ 
    , LANGUAGETYPE 
    , LANGUAGETITLE 
    , LANGUAGESCORE
    , LANGUAGEORG
    , LANGUAGEDATE 	

)VALUES(

	LANGUAGESEQ.NEXTVAL 
    , #{resumeSeq}
    , #{languageType}
    , #{languageTitle}
    , #{languageScore}
    , #{languageOrg}
    , #{languageDate}

)


INSERT INTO CAREER(

    CAREERSEQ 
    , RESUMESEQ 
    , COMPANYNAME 
    , CAREERCATEGORY 
    , CAREERSTARTDATE 
    , CAREERENDDATE 
    , CAREERDEPARTMENT 
    , CAREERPOSITION 
    , CAREERREGION 
    , CAREERSALARY
    , CAREERTASK
    , CAREERDETAILS

)VALUES(

	CAREERSEQ.NEXTVAL
	,#{resumeSeq}
	,#{companyName}
	,#{careerCategory}
	,#{careerStartDate}
	,#{careerEndDate}
	,#{careerDepartment}
	,#{careerPosition}
	,#{careerRegion}
	,#{careerSalary}
	,#{careerTask}
	,#{careerDetails}

)

mapper.select:
SELECT BASICSEQ ,RESUMESEQ ,PERSONID ,PERSONNAME
,BIRTHDATE ,GENDER ,PERSONEMAIL ,PERSONPHONE ,PERSONADDR
FROM BASICINFO WHERE RESUMESEQ=#{resumeSeq}

SELECT
LANGUAGESEQ , RESUMESEQ , LANGUAGETYPE , LANGUAGETITLE , LANGUAGESCORE , LANGUAGEORG , TO_CHAR(LANGUAGEDATE, 'YYYY-MM-DD') LANGUAGEDATE
FROM LANGUAGESCORE WHERE RESUMESEQ=#{resumeSeq}

SELECT
ACTIVITYSEQ ,RESUMESEQ ,ACTIVITYTITLE ,ACTIVITYORG ,ACTIVITYDETAILS
FROM Activity WHERE RESUMESEQ=#{resumeSeq}
